import logo from "./assets/investment-calculator-logo.png";
import SubmitForm from "./components/SubmitForm";
import { useState } from "react";
import TableInfo from "./components/TableInfo";

function App() {
  const [data, setData] = useState(null);
  const [userInput, setUserInput] = useState(null);
  const calculateHandler = (userInput) => {
    setUserInput(userInput);

    const yearlyData = []; // per-year results

    let currentSavings = +userInput.currentSavingsData; // feel free to change the shape of this input object!
    const yearlyContribution = +userInput.yearlySavingsData; // as mentioned: feel free to change the shape...
    const expectedReturn = +userInput.expectedInterestData / 100;
    const duration = +userInput.investmentDurationData;

    // The below code calculates yearly results (total savings, interest etc)
    for (let i = 0; i < duration; i++) {
      const yearlyInterest = currentSavings * expectedReturn;
      currentSavings += yearlyInterest + yearlyContribution;
      yearlyData.push({
        // feel free to change the shape of the data pushed to the array!
        year: i + 1,
        yearlyInterest: yearlyInterest,
        savingsEndOfYear: currentSavings,
        yearlyContribution: yearlyContribution,
      });
    }
    setData(yearlyData);
  };

  return (
    <div>
      <header className="header">
        <img src={logo} alt="logo" />
        <h1>Investment Calculator</h1>
      </header>

      <SubmitForm onCalculate={calculateHandler} />

      {/* Todo: Show below table conditionally (only once result data is available) */}
      {/* Show fallback text if no data is available */}
      {data && (
        <TableInfo
          yearlyData={data}
          initialInvestment={userInput.currentSavings}
        />
      )}
    </div>
  );
}

export default App;
